    --gradient-primary: linear-gradient(135deg, hsl(16 85% 60%), hsl(30 90% 65%));
    --gradient-success: linear-gradient(135deg, hsl(142 70% 45%), hsl(160 60% 50%));
    --gradient-danger: linear-gradient(135deg, hsl(0 72% 51%), hsl(16 85% 55%));
    --gradient-background: linear-gradient(180deg, hsl(30 50% 98%), hsl(30 40% 95%));

    /* Shadows */
    --shadow-soft: 0 4px 20px -4px hsl(16 85% 60% / 0.2);
    --shadow-glow: 0 0 30px hsl(16 85% 60% / 0.3);
    --shadow-success: 0 4px 20px -4px hsl(142 70% 45% / 0.3);

    /* Animations */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dark {
    --background: 20 15% 8%;
    --foreground: 30 20% 95%;
    --card: 20 15% 12%;
    --card-foreground: 30 20% 95%;
    --popover: 20 15% 12%;
    --popover-foreground: 30 20% 95%;
    --primary: 16 85% 60%;
    --primary-foreground: 0 0% 100%;
    --secondary: 20 10% 18%;
    --secondary-foreground: 30 20% 90%;
    --muted: 20 10% 18%;
    --muted-foreground: 30 10% 55%;
    --accent: 142 70% 45%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;
    --border: 20 10% 20%;
    --input: 20 10% 20%;
    --ring: 16 85% 60%;

    --warning: 45 93% 47%;
    --warning-foreground: 20 20% 10%;
    --success: 142 70% 45%;
    --success-foreground: 0 0% 100%;

    --gradient-primary: linear-gradient(135deg, hsl(16 85% 55%), hsl(30 90% 60%));
    --gradient-success: linear-gradient(135deg, hsl(142 70% 40%), hsl(160 60% 45%));
    --gradient-danger: linear-gradient(135deg, hsl(0 72% 45%), hsl(16 85% 50%));
    --gradient-background: linear-gradient(180deg, hsl(20 15% 8%), hsl(20 15% 6%));

    --shadow-soft: 0 4px 20px -4px hsl(16 85% 60% / 0.15);
    --shadow-glow: 0 0 30px hsl(16 85% 60% / 0.2);
    --shadow-success: 0 4px 20px -4px hsl(142 70% 45% / 0.2);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

@layer utilities {
  .bg-gradient-primary {
    background: var(--gradient-primary);
  }
  .bg-gradient-success {
    background: var(--gradient-success);
  }
  .bg-gradient-danger {
    background: var(--gradient-danger);
  }
  .bg-gradient-background {
    background: var(--gradient-background);
  }
  .shadow-soft {
    box-shadow: var(--shadow-soft);
  }
  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }
  .shadow-success {
    box-shadow: var(--shadow-success);
  }
  .transition-smooth {
    transition: var(--transition-smooth);
  }

  @keyframes pulse-glow {
    0%, 100% {
      box-shadow: 0 0 20px hsl(16 85% 60% / 0.4);
    }
    50% {
      box-shadow: 0 0 40px hsl(16 85% 60% / 0.6);
    }
  }

  @keyframes bounce-gentle {
    0%, 100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-5px);
    }
  }

  @keyframes fade-in {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .animate-bounce-gentle {
    animation: bounce-gentle 2s ease-in-out infinite;
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease-out forwards;
  }
}

