import { useEffect, useState } from 'react';
import { Clock, AlertTriangle } from 'lucide-react';

interface CountdownTimerProps {
  lastCheckin: string | null;
  deadlineHours?: number;
}

export function CountdownTimer({ lastCheckin, deadlineHours = 48 }: CountdownTimerProps) {
  const [timeLeft, setTimeLeft] = useState<{ hours: number; minutes: number; seconds: number; isUrgent: boolean; isExpired: boolean } | null>(null);

  useEffect(() => {
    if (!lastCheckin) {
      setTimeLeft(null);
      return;
    }

    const calculateTimeLeft = () => {
      const lastCheckinTime = new Date(lastCheckin).getTime();
      const deadline = lastCheckinTime + deadlineHours * 60 * 60 * 1000;
      const now = Date.now();
      const diff = deadline - now;

      if (diff <= 0) {
        return { hours: 0, minutes: 0, seconds: 0, isUrgent: true, isExpired: true };
      }

      const hours = Math.floor(diff / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((diff % (1000 * 60)) / 1000);
      const isUrgent = hours < 12;

      return { hours, minutes, seconds, isUrgent, isExpired: false };
    };

    setTimeLeft(calculateTimeLeft());
    const interval = setInterval(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);

    return () => clearInterval(interval);
  }, [lastCheckin, deadlineHours]);

  if (!timeLeft) {
    return (
      <div className="text-center p-6 bg-card rounded-2xl border border-border">
        <p className="text-muted-foreground">还没有签到记录</p>
      </div>
    );
  }

  const formatTime = (num: number) => num.toString().padStart(2, '0');

  return (
    <div className={`text-center p-6 rounded-2xl border transition-smooth ${
      timeLeft.isExpired
        ? 'bg-destructive/10 border-destructive/30'
        : timeLeft.isUrgent
          ? 'bg-warning/10 border-warning/30'
          : 'bg-card border-border'
    }`}>
      <div className="flex items-center justify-center gap-2 mb-3">
        {timeLeft.isUrgent ? (
          <AlertTriangle className="w-5 h-5 text-destructive" />
        ) : (
          <Clock className="w-5 h-5 text-muted-foreground" />
        )}
        <span className="text-sm text-muted-foreground">
          {timeLeft.isExpired ? '已超时！紧急联系人将收到通知' : '距离下次必须签到还有'}
        </span>
      </div>

      <div className="flex items-center justify-center gap-2 text-4xl font-bold font-mono">
        <span className={timeLeft.isUrgent ? 'text-destructive' : 'text-foreground'}>
          {formatTime(timeLeft.hours)}
        </span>
        <span className="text-muted-foreground">:</span>
        <span className={timeLeft.isUrgent ? 'text-destructive' : 'text-foreground'}>
          {formatTime(timeLeft.minutes)}
        </span>
        <span className="text-muted-foreground">:</span>
        <span className={timeLeft.isUrgent ? 'text-destructive' : 'text-foreground'}>
          {formatTime(timeLeft.seconds)}
        </span>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { UserPlus, X, Mail, User } from 'lucide-react';
import type { EmergencyContact } from '@/types';

interface EmergencyContactFormProps {
  contacts: EmergencyContact[];
  onAddContact: (contact: Omit<EmergencyContact, 'id'>) => void;
  onRemoveContact: (id: string) => void;
}

export function EmergencyContactForm({ contacts, onAddContact, onRemoveContact }: EmergencyContactFormProps) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [isAdding, setIsAdding] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name.trim() && email.trim()) {
      onAddContact({ name: name.trim(), email: email.trim() });
      setName('');
      setEmail('');
      setIsAdding(false);
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-foreground">紧急联系人</h3>
        {!isAdding && (
          <Button variant="outline" size="sm" onClick={() => setIsAdding(true)}>
            <UserPlus className="w-4 h-4 mr-1" />
            添加
          </Button>
        )}
      </div>

      {isAdding && (
        <form onSubmit={handleSubmit} className="p-4 bg-secondary/50 rounded-xl space-y-4 animate-fade-in">
          <div className="space-y-2">
            <Label htmlFor="name" className="text-sm text-muted-foreground">姓名</Label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="联系人姓名"
                className="pl-10"
                required
              />
            </div>
          </div>
          <div className="space-y-2">
            <Label htmlFor="email" className="text-sm text-muted-foreground">邮箱</Label>
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="contact@example.com"
                className="pl-10"
                required
              />
            </div>
          </div>
          <div className="flex gap-2">
            <Button type="submit" size="sm">保存</Button>
            <Button type="button" variant="ghost" size="sm" onClick={() => setIsAdding(false)}>取消</Button>
          </div>
        </form>
      )}

      {contacts.length === 0 && !isAdding ? (
        <div className="text-center py-8 text-muted-foreground">
          <p>还没有添加紧急联系人</p>
          <p className="text-sm">添加联系人后，如果您连续两天未签到，系统会自动通知他们</p>
        </div>
      ) : (
        <div className="space-y-2">
          {contacts.map((contact) => (
            <div
              key={contact.id}
              className="flex items-center justify-between p-4 bg-card rounded-xl border border-border transition-smooth hover:shadow-soft"
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center">
                  <User className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="font-medium text-foreground">{contact.name}</p>
                  <p className="text-sm text-muted-foreground">{contact.email}</p>
                </div>
              </div>
              <Button
                variant="ghost"
                size="icon"
                onClick={() => onRemoveContact(contact.id)}
                className="text-muted-foreground hover:text-destructive"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

import { Calendar, CheckCircle } from 'lucide-react';
import type { CheckinRecord } from '@/types';

interface CheckinHistoryProps {
  checkins: CheckinRecord[];
}

export function CheckinHistory({ checkins }: CheckinHistoryProps) {
  const recentCheckins = checkins.slice(-7).reverse();

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('zh-CN', {
      month: 'short',
      day: 'numeric',
      weekday: 'short',
    });
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp).toLocaleTimeString('zh-CN', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-2">
        <Calendar className="w-5 h-5 text-primary" />
        <h3 className="text-lg font-semibold text-foreground">签到记录</h3>
      </div>

      {recentCheckins.length === 0 ? (
        <div className="text-center py-8 text-muted-foreground">
          <p>暂无签到记录</p>
        </div>
      ) : (
        <div className="space-y-2">
          {recentCheckins.map((record, index) => (
            <div
              key={record.date}
              className="flex items-center justify-between p-3 bg-card rounded-xl border border-border"
              style={{ animationDelay: `${index * 0.05}s` }}
            >
              <div className="flex items-center gap-3">
                <CheckCircle className="w-5 h-5 text-accent" />
                <span className="text-foreground">{formatDate(record.date)}</span>
              </div>
              <span className="text-sm text-muted-foreground">{formatTime(record.timestamp)}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

